import { useEffect, useState } from "react";
import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss";
import { sidePortfolio, mainPortfolio, dessertPortfolio } from "../../data";

export default function Portfolio({ eyeClosed, setEyeClosed }) {
  const [selected, setSelected] = useState("web");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "side",
      title: "Side/Salad",
    },
    {
      id: "main",
      title: "Main",
    },
    {
      id: "dessert",
      title: "Dessert",
    },
  ];

  useEffect(()=>{
    switch(selected){
      case "side":
      setData(sidePortfolio);
      break;
      case "main":
      setData(mainPortfolio);
      break;
      case "dessert":
      setData(dessertPortfolio);
      break;
      default:
      setData(sidePortfolio);
    }

  },[selected])

  return (
    <div className={"portfolio " + (eyeClosed && "active")} id="portfolio">
      <h1 onClick={() => setEyeClosed(!eyeClosed)}>Recipes:</h1>
      <ul>
        {list.map((item) => (
          <PortfolioList
          title={item.title}
          active={selected === item.id}
          setSelected={setSelected}
          id={item.id}
          />
        ))}
      </ul>
      <div className="container">
        {data.map(d=>(


        <div className="item">
              <img src={d.img} alt={d.title} />
            <h3>{d.title}</h3>
        </div>
        ))}
      </div>
      <blockquote class="tiktok-embed" cite="https://www.tiktok.com/@dinnerwithdan/video/7059457689541299462" data-video-id="7059457689541299462" style="max-width: 605px;min-width: 325px;" > <section> <a target="_blank" title="@dinnerwithdan" href="https://www.tiktok.com/@dinnerwithdan">@dinnerwithdan</a> <p>Two hour ramen + homemade noodles // Hot Coffee by Ghostrifter Official | https://soundcloud.com/ghostrifter-official</p> <a target="_blank" title="♬ original sound - Daniel Curtis" href="https://www.tiktok.com/music/original-sound-7059457676417616645">♬ original sound - Daniel Curtis</a> </section> </blockquote> <script async src="https://www.tiktok.com/embed.js"></script>
    </div>
  )
}
