import { useEffect, useState } from "react";
import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss";
import { featuredPortfolio, webPortfolio, mobilePortfolio } from "../../data";

export default function Portfolio() {
  const [selected, setSelected] = useState("featured");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "featured",
      title: "featured",
    },
    {
      id: "web",
      title: "Web App",
    },
    {
      id: "mobile",
      title: "Mobile App",
    },
  ];

  useEffect(()=>{

    switch(selected){
      case "featured":
        setData(featuredPortfolio);
        break;
        case "featured":
        setData(webPortfolio);
        break;
        case "featured":
        setData(mobilePortfolio);
        break;
    }

  },[selected])

  return (
    <div className="portfolio" id="portfolio">
      <h1>Portfolio</h1>
      <ul>
        {list.map((item) => (
          <PortfolioList
          title={item.title}
          active={selected === item.id}
          setSelected={setSelected}
          id={item.id}
          />
        ))}
      </ul>
      <div className="container">
        <div className="item">
          <a href="http://be-natural-project.herokuapp.com/">
            <img src="assets/BeNatural.png" alt="BeNatural" />
          </a>
            <h3>BeNatural</h3>
        </div>
        <div className="item">
          <a href="https://la-casserole-new.herokuapp.com/chefs">
            <img src="assets/LaCasserole.png" alt="LaCasserole" />
          </a>
          <h3>LaCasserole</h3>
        </div>
        <div className="item">
          <a href="https://www.daniel-curtis.co.uk/">
            <img src="assets/Portfolio.png" alt="Portfolio"/>
          </a>
          <h3>Portfolio</h3>
        </div>
      </div>
    </div>
  )
}
